mapper.select:
SELECT bh.bill_id AS journalEntry, DATE_FORMAT(bh.billing_date, '%Y-%m-%d') AS journalEntryDate, c.name AS account, COALESCE(bh.net, bh.gross, so.net_value, so.gross_value, 0.0) AS amount, COALESCE(so.currency, 'USD') AS amountUnit, 'Invoice' AS journalEntryType, DATE_FORMAT(bh.billing_date, '%Y-%m-%d') AS netDueDate, CONCAT('Bill ID: ', bh.bill_id) AS assignment, COALESCE(c.company_code, '1000') AS companyCode FROM erp_billing_hdr bh JOIN erp_customer c ON bh.customer_id = c.customer_id LEFT JOIN erp_outbound_delivery od ON bh.dlv_id = od.dlv_id LEFT JOIN erp_sales_order_hdr so ON od.so_id = so.so_id WHERE bh.status = 'OPEN'
AND c.company_code = #{generalInformation.companyCode}
AND bh.customer_id = #{generalInformation.customerID}
AND COALESCE(so.currency, 'USD') = #{currency}
ORDER BY bh.billing_date LIMIT 100

mapper.update:
UPDATE erp_billing_hdr
SET status = 'CLEAR'
WHERE bill_id = #{billId}

UPDATE erp_billing_hdr
SET status = 'OPEN'
WHERE bill_id = #{billId}

mapper.select:
SELECT customer_id
FROM erp_customer
WHERE customer_id = #{accountId}
   OR name = #{accountId}
   OR company_code = #{accountId}
LIMIT 1
